% --- Executes just before meyvetani is made visible.
function meyvetani_OpeningFcn(hObject, eventdata, handles, varargin)
handles.output = hObject;

%arka plan resim ekleme
% create an axes that spans the whole gui
ah = axes('unit', 'normalized', 'position', [0 0 1 1]); 
% import the background image and show it on the axes
bg = imread('a.jpg'); imagesc(bg);
% prevent plotting over the background and turn the axis off
set(ah,'handlevisibility','off','visible','off')
% making sure the background is behind all the other uicontrols
uistack(ah, 'bottom');

% Update handles structure
guidata(hObject, handles);
function varargout = meyvetani_OutputFcn(hObject, eventdata, handles) 
varargout{1} = handles.output;


